mapper.select:
select f.NAME, (select u.`NAME` from ACT_HI_ACTINST ht1 left join ACT_HI_VARINST hv1 on ht1.TASK_ID_ = hv1.TASK_ID_ LEFT JOIN sys_user u ON u.USERNAME=ht1.ASSIGNEE_ where ht1.PROC_INST_ID_ = f.PROC_INST_ID_ AND ht1.ACT_ID_='QJ2' order by ht1.ID_ DESC LIMIT 1) as TONAME, f.COPYNAME, f.STARTTIME, (select END_TIME_ from ACT_HI_ACTINST ht1 left join ACT_HI_VARINST hv1 on ht1.TASK_ID_ = hv1.TASK_ID_ where ht1.PROC_INST_ID_ = f.PROC_INST_ID_ order by ht1.ID_ DESC LIMIT 1) as ENDTIME, (select ACT_NAME_ from ACT_HI_ACTINST ht1 left join ACT_HI_VARINST hv1 on ht1.TASK_ID_ = hv1.TASK_ID_ where ht1.PROC_INST_ID_ = f.PROC_INST_ID_ order by ht1.ID_ DESC LIMIT 1) STATE, f.PROC_INST_ID_, f.STUDYPLAN_ID from f where 1=1
and f.NAME = #{pd.NAME}
and (
)
ORDER BY f.STARTTIME DESC
